INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (668, 'Direttiva (UE) 2016/2284 concernente la riduzione delle emissioni nazionali di determinati inquinanti atmosferici, che modifica la direttiva 2003/35/CE e abroga la direttiva 2001/81/CE', 'Direttiva (UE)', '2016/2284', 'La direttiva stabilisce gli impegni di riduzione delle emissioni per le emissioni atmosferiche antropogeniche degli Stati membri di biossido di zolfo (SO2), ossidi di azoto (NOx), composti organici volatili non metanici (COVNM), ammoniaca (NH3), e particolato fine (PM2,5) e impone l''elaborazione, l''adozione e l''attuazione di programmi nazionali di controllo dell''inquinamento atmosferico e il monitoraggio e la comunicazione in merito ai suddetti inquinanti e agli altri inquinanti indicati all''allegato I e ai loro effetti.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva concernente alla riduzione delle emissioni nazionali di determinati inquinanti atmosferici', 'Direttiva (UE) - 2016/2284', 'Direttiva (UE) 2016/2284', NULL);

