INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (668, 'GRI 305- Emissioni (2016)', 'GRI', '305', 'Questo Standard tratta il tema delle emissioni nell’aria – sostanze scaricate da una fonte nell’atmosfera. I vari tipi di emissioni sono: gas a effetto serra (GHG), sostanze che riducono lo strato di ozono (ODS, ozone-depleting substances), ossidi di azoto (NO) e ossidi di zolfo (SO), oltre ad altre emissioni nell’aria di entità notevole.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Emissioni (2016)', 'GRI - 305 (2016)', 'GRI 305-7 (2016)', 'Ossidi di azoto (NOx), ossidi di zolfo (SOx) e altre emissioni nell’aria rilevanti');

-- Record 2418/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (668, 'Linee guida globali OMS sulla qualità dell''aria', 'Linee Guida (Int.)', 'OMS', 'Le Linee guida globali OMS sulla qualità dell''aria sono strumenti fondamentali per proteggere la salute pubblica, guidare i decisori politici e aumentare la consapevolezza pubblica sui rischi dell''inquinamento atmosferico. Forniscono valori soglia scientificamente supportati per diversi inquinanti chiave, che se rispettati, possono ridurre significativamente l''incidenza di malattie legate all''inquinamento dell''aria.', 'Le linee guida sono documenti che forniscono indicazioni e raccomandazioni su come applicare le leggi esistenti o sviluppare politiche in un determinato settore.', 'Linee guida globali sulla qualità dell''aria', 'Linee Guida - OMS', 'LGG OMS qualità dell''aria', 'Linee guida globali OMS sulla qualità dell''aria');

-- Record 2419/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (668, 'Piano d''azione dell''UE: "Verso l''inquinamento zero per l''aria, l''acqua e il suolo"', 'Piano Azione (UE)', 'Inquinamento Zero', 'Il piano d''azione per l''inquinamento zero dell''UE, adottato nel 2021, mira a ridurre l''inquinamento atmosferico e acustico a livelli non più considerati nocivi per la salute e per gli ecosistemi naturali.', 'Un piano di azione a livello dell''Unione Europea (UE) è un documento strategico che definisce obiettivi specifici e misure concrete per affrontare sfide o perseguire opportunità a livello comunitario. Questi piani sono sviluppati e implementati dall''UE per guidare l''azione politica e normativa su questioni di interesse comune tra gli Stati membri.', 'Piano Verso l''inquinamento zero per l''aria, l''acqua e il suolo"', 'Piano Azione (UE) - Inquinamento Zero', 'Piano Azione (UE) Inquinamento Zero', 'COM(2021) 400 - Piano d''azione dell''UE: "Verso l''inquinamento zero per l''aria, l''acqua e il suolo"');

-- Record 2420/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (668, 'Programma nazionale controllo Inquinamento Atmosferico -PNCIA - (ob. 2020-2030)', 'Programma Nazionale (IT)', 'Controllo Inquinamento Atmosferico', 'L''obiettivo del Programma Nazionale Controllo Inquinamento Atmosferico (PNCIA) è quello di monitorare, prevenire e ridurre l''inquinamento dell''aria in Italia. Questo programma è stato istituito per garantire il rispetto degli standard ambientali e per migliorare la qualità dell''aria, proteggendo la salute pubblica e l''ambiente.', 'Un programma nazionale è un piano strategico e organizzato definito a livello nazionale per affrontare specifici obiettivi e sfide in diversi settori della società.', 'Programma per il controllo dell''inquinamento atmosferico (ob. 2020-2030)', 'Programma Nazionale - Controllo Inquinamento Atmosferico', 'PNCIA', 'Programma nazionale controllo Inquinamento Atmosferico');

