INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (671, 'ISO 14046:2014: Impronta Idrica', 'ISO', '14046:2014', 'La ISO 14046:2014 è uno standard internazionale che fornisce linee guida per la valutazione e la comunicazione dell''impronta idrica di un''organizzazione, di un prodotto o di un servizio. Questo standard è parte della serie ISO 14000, che si occupa della gestione ambientale.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Impronta Idrica', 'ISO - 14046:2014', 'ISO 14046:2014', 'Impronta Idrica');

-- Record 2447/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (671, 'Piano di Gestione delle Acque (2021- 2027)', 'Piano Nazionale (IT)', 'Gestione delle Acque', 'Il Piano di Gestione delle Acque, previsto dalla Direttiva 2000/60/CE, è lo strumento principale dell''Autorità di bacino distrettuale per raggiungere il buono stato ambientale dei corpi idrici e garantire una gestione sostenibile delle risorse idriche, riducendo l''inquinamento, prevenendo il deterioramento e migliorando gli ecosistemi acquatici e terrestri.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Piano di gestione delle acque 2021-2027', 'Piano Nazionale (IT) - Gestione delle Acque', 'Piano (IT) Gestione Acque', 'Piano di Gestione delle Acque (2021- 2027');

-- Record 2448/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (671, 'WEF- Metriche del Capitalismo degli Stakeholder 2020 P2-3 (Disponibilità di acqua dolce)', 'WEF', '4P: Metrica P2-3', 'Questa metrica si concentra sull''acqua dolce, essenziale per la salute dell''ambiente, lo sviluppo economico e come diritto umano fondamentale. Tuttavia, in regioni con scarsità d''acqua e infrastrutture inadeguate, questo diritto spesso non è garantito.', 'Il World Economic Forum è l''Organizzazione Internazionale per la Cooperazione Pubblico-Privata. L''WEF insieme ad altre istituzioni ha sviluppato un insieme di metriche comuni per la rendicontazione della creazione di valore sostenibile (21 metriche principali e 34 metriche e divulgazioni ampliate) raggruprate in 4 pilastri principali (Principle of Governance, Planet, Persone and Prosperity). Per maggiore informazioni sulle 4 Pilastri (Metriche) di sostenibilità del WEF andare su: https://www.weforum.org/stakeholdercapitalism/our-metrics/', '3- Disponibilità di acqua dolce', 'WEF (MSC2024) 4P- P2-3', 'WEF (MSC2024) 4P- P2-3 CM 1', 'Consumo e prelievo di acqua in aree a stress idrico');

