INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (671, 'Direttiva (UE) 2020/2184 del Parlamento europeo e del Consiglio del 16 dicembre 2020 concernente la qualità delle acque destinate al consumo umano', 'Direttiva (UE)', '2020/2184', 'Questa direttiva riguarda la qualità delle acque destinate al consumo umano per tutti nell’Unione. Gli obiettivi della direttiva sono la protezione della salute umana dagli effetti negativi derivanti dalla contaminazione delle acque destinate al consumo umano, garantendone la salubrità e la pulizia, nonché il miglioramento dell’accesso alle acque destinate al consumo umano.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva concernente la qualità delle acque destinate al consumo umano', 'Direttiva (UE) - 2020/2184', 'Direttiva (UE) 2020/2184', NULL);

-- Record 2450/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (671, 'Global Compact delle Nazioni Unite (UNGC): Principio 7- Ambiente', 'UNGC', 'Principio 7', 'Le imprese dovrebbero sostenere un approccio precauzionale alle sfide ambientali', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Ambiente', 'UNGC - Principio 7', 'UNGC P.7', 'Le imprese dovrebbero sostenere un approccio precauzionale alle sfide ambientali');

