INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (672, 'ISO 14046:2014: Impronta Idrica', 'ISO', '14046:2014', 'La ISO 14046:2014 è uno standard internazionale che fornisce linee guida per la valutazione e la comunicazione dell''impronta idrica di un''organizzazione, di un prodotto o di un servizio. Questo standard è parte della serie ISO 14000, che si occupa della gestione ambientale.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Impronta Idrica', 'ISO - 14046:2014', 'ISO 14046:2014', 'Impronta Idrica');

