INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (673, 'SDGs: Goal 7 Energia pulita e accessibile', 'SDGs', 'Goal 7', 'Assicurare a tutti l’accesso a sistemi di energia economici, affidabili, sostenibili e moderni', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Energia pulita e accessibile', 'SDGs - Goal 7', 'SDG 7.2', 'Aumentare considerevolmente entro il 2030 la quota di energie rinnovabili nel consumo totale di energia');

-- Record 2481/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (673, 'SASB Standard- Settore immobiliare (2018) (IF-RE)', 'SASB', 'IF-RE', 'Gli Standard SASB sono progettati per identificare e standardizzare la divulgazione delle questioni di sostenibilità più rilevanti per le decisioni degli investitori in ciascuno dei 77 settori industriali. Lo standard dell''industria Settore immobiliare rientra nel settore Infrastrutture.', 'Il Sustainability Accounting Standards Board (SASB) è un''organizzazione no-profit e indipendente che stabilisce gli standard. Gli standard SASB aiutano le aziende a divulgare informazioni rilevanti sulla sostenibilità ai loro investitori.
  
  Gli Standard SASB comprendono una vasta gamma di rischi e opportunità legati alla sostenibilità, inclusi informazioni ambientali e sociali così come governance operativa come la Gestione della Catena di Approvvigionamento, la Gestione del Rischio Sistemico ed Etica Aziendale, tra gli altri.
 https://sasb.ifrs.org/"', 'Settore immobiliare (2018)', 'SASB 2018: IF-RE', 'SASB 2018: IF-RE-130a.2-3', 'Percentuale del consumo di energia proveniente da fonti rinnovabili');

-- Record 2482/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (673, 'ESRS E1- Cambiamento climatico', 'ESRS', 'E1', 'Questo Standard copre i requisiti di Informativa GRI  relativi ai seguenti aspetti della sostenibilità: "Mitigazione del cambiamento climatico" e "Adattamento al cambiamento climatico". Copre anche le questioni energetiche, nella misura in cui sono rilevanti per il cambiamento climatico.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Cambiamento climatico', 'ESRS - E1', 'ESRS (E1) E1-5 - 35b', 'Consumo e mix energetico');

