INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (675, 'Direttiva 2008/98/CE del Parlamento europeo e del Consiglio, del 19 novembre 2008, relativa ai rifiuti e che abroga alcune direttive', 'Direttiva (UE)', '2008/98/CE', 'Questa direttiva stabilisce misure volte a proteggere l’ambiente e la salute umana evitando o riducendo la produzione di rifiuti, gli effetti negativi della produzione e della gestione dei rifiuti, riducendo gli effetti generali dell’uso delle risorse e migliorandone l’efficienza, che costitui scono elementi fondamentali per il passaggio a un’economia circolare e per assicurare la competitività a lungo termine dell’Unione.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Relativa ai rifiuti', 'Direttiva (UE) - 2008/98/CE', 'Direttiva (UE) 2008/98/CE', NULL);

