INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (678, 'ESRS G1- Condotta aziendale', 'ESRS', 'G1', 'L''obiettivo di questo Standard è specificare i requisiti di divulgazione che consentiranno agli utenti delle dichiarazioni di sostenibilità dell''azienda di comprendere la strategia e l''approccio dell''azienda, i processi e le procedure, nonché le sue performance riguardo alla condotta aziendale.', 'L''obiettivo degli Standard Europei di Reporting sulla Sostenibilità (ESRS) è specificare le informazioni sulla sostenibilità che un''impresa deve divulgare in conformità alle normative della Unione Europea.Gli ESRS coprono l''intera gamma di questioni ambientali, sociali e di governance, compresi cambiamenti climatici, biodiversità e diritti umani. Forniscono informazioni agli investitori per comprendere l''impatto sulla sostenibilità delle aziende in cui investono. Per maggiori informazioni accedere al link: https://www.efrag.org/lab6?AspxAutoDetectCookieSupport=1', 'Condotta aziendale', 'ESRS - G1', 'ESRS (G1) G1-4 25a', 'Numero di incidenti confermati di corruzione o concussione');

-- Record 2519/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (678, 'Global Compact delle Nazioni Unite (UNGC): Principio 10- anticorruzione', 'UNGC', 'Principio 10', 'Le imprese dovrebbero impegnarsi contro la corruzione in tutte le sue forme, comprese l''estorsione e la concussione.', 'Il Global Compact delle Nazioni Unite (UNGC) è la più grande iniziativa di sostenibilità aziendale al mondo. L''UNGC ha sviluppato 10 principi per aiutare le aziende a operare in modo sostenibile e responsabile. I 10 Principi del Global Compact delle Nazioni Unite (UNGC) sono una serie di linee guida per le imprese, focalizzate su quattro aree principali: diritti umani, lavoro, ambiente e anti-corruzione. Per maggiore informazione consultare: https://unglobalcompact.org/what-is-gc/mission/principles', 'Anticorruzione', 'UNGC - Principio 10', 'UNGC P.10', 'Le imprese dovrebbero impegnarsi contro la corruzione in tutte le sue forme, comprese l''estorsione e la concussione.');

-- Record 2520/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (678, 'Linee guida dell''OCSE per le imprese multinazionali sulla condotta responsabile delle imprese: Principio 5', 'OECD', 'Linee guida - Principio 5', 'Il principio 5 stabilisce che le imprese dovrebbero: Le imprese dovrebbero astenersi dal ricercare o dall’accettare esenzioni non contemplate nelle norme di legge o regolamentari relative ai diritti umani, all’ambiente, alla salute, alla sicurezza, al lavoro, alla fiscalità, agli incentivi finanziari o ad altre materie.', 'Le Linee guida dell''OCSE per le imprese multinazionali sono raccomandazioni governative rivolte alle aziende, volte a promuovere una condotta responsabile. L''obiettivo è incentivare contributi positivi al progresso economico, ambientale e sociale, riducendo al contempo gli impatti negativi legati alle operazioni, ai prodotti e ai servizi aziendali. Le Linee guida coprono tutte le principali aree di responsabilità d''impresa, tra cui diritti umani, diritti dei lavoratori, ambiente, corruzione, interessi dei consumatori, trasparenza, scienza e tecnologia, concorrenza e fiscalità.', 'Linee guida per le imprese multinazionali sulla condotta responsabile delle imprese', 'OECD - Linee guida - Principio 5', 'Linee guida OECD P.5', 'Le imprese dovrebbero astenersi dal ricercare o dall’accettare esenzioni non contemplate nelle norme di legge o regolamentari relative ai diritti umani, all’ambiente, alla salute, alla sicurezza, al lavoro, alla fiscalità, agli incentivi finanziari o ad altre materie.');

-- Record 2521/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (678, 'Direttiva (UE) 2022/2464 del Parlamento europeo e del Consiglio del 14 dicembre 2022 che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE)', '2022/2464', 'La CSRD o Corporate Sustainability Reporting Directive (Direttiva 2022/2464) è la direttiva europea che mira a migliorare la trasparenza e la qualità delle informazioni sulla sostenibilità fornite dalle aziende. La CSRD sostituisce e amplia la Non-Financial Reporting Directive (NFRD), aumentando le esigenze di rendicontazione per le imprese riguardo alle loro attività sostenibili. I punti chiavi della CSRD sono: -L''ampiezza del campo di applicazione rispetto alla NFRD rispetto alle aziende che devono rendicontare, - le informazioni da rendicontare in termini ESG e di sostenibilità; - lo standard di rendicontazione (gli ESRS); - L''audit e certificazione; e la digitalizzazione delle informazioni. 
 
 L''obiettivo principale della CSRD è migliorare la trasparenza e responsabilizzare le aziende verso pratiche più sostenibili, aiutando investitori, consumatori, e altre parti interessate a prendere decisioni informate basate su dati di sostenibilità accurati e completi.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva che modifica il regolamento (UE) n. 537/2014, la direttiva 2004/109/CE, la direttiva 2006/43/CE e la direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Direttiva (UE) - 2022/2464', 'CSRD', 'Rendicontazione societaria di sostenibilità (CSRD)');

-- Record 2522/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (678, 'ISO 37001: Sistemi di gestione per la prevenzione della corruzione', 'ISO', '37001', 'L''ISO 37001 è uno standard internazionale che fornisce linee guida e requisiti per l''implementazione di un sistema di gestione antisovvenzione (Anti-Bribery Management System, ABMS). L''obiettivo principale è quello di aiutare le organizzazioni a stabilire, implementare, mantenere, aggiornare e migliorare un sistema di gestione efficace per prevenire la corruzione e aderire ai requisiti legali e regolamentari applicabili.', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Sistemi di gestione per la prevenzione della corruzione', 'ISO - 37001', 'ISO 37001', 'Sistemi di gestione per la prevenzione della corruzione');

-- Record 2523/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (678, 'Decreto Legislativo 6 settembre 2011, n. 159 (Codice delle leggi antimafia e delle misure di prevenzione, nonche'' nuove disposizioni in materia di documentazione antimafia, a norma degli articoli 1 e 2 della legge 13 agosto 2010, n. 136)', 'Decreto (IT)', '159', 'Il D. Lgs. 159 stabilisce il Codice delle leggi antimafia e delle misure di prevenzione. Il decreto mira a migliorare l''efficacia delle indagini e delle azioni preventive contro la criminalità organizzata, contribuendo alla sicurezza e al rispetto delle norme nel contesto italiano.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Codice delle leggi antimafia e delle misure di prevenzione, nonche'' nuove disposizioni in materia di documentazione antimafia', 'Decreto Legislativo - 159', 'D.Lgs. 159/2011', NULL);

-- Record 2524/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (678, 'Direttiva (UE) 2017/1371 del Parlamento europeo e del Consiglio, del 5 luglio 2017, relativa alla lotta contro la frode che lede gli interessi finanziari dell''Unione mediante il diritto penale', 'Direttiva (UE)', '2017/1371', 'La Direttiva (UE) 2017/1371, nota anche come la Direttiva PIF (Protezione degli Interessi Finanziari dell''Unione), è una normativa europea che mira a combattere la frode e altre attività illegali che ledono gli interessi finanziari dell''Unione Europea mediante il diritto penale. La direttiva stabilisce norme minime relative alla definizione dei reati e delle sanzioni in materia di frode che colpisce il bilancio dell''UE.', 'Una direttiva costituisce un atto giuridico adottato dalle istituzioni dell’Unione europea e rivolto agli Stati membri dell’Unione, ha carattere vincolante per quanto riguarda il risultato e obiettivi da raggiungere. Le autorità nazionali di ogni paese dell’Unione europea cui si rivolge la direttiva, hanno il compito di definire la forma e i metodi da impiegare per l’integrazione della direttiva nel proprio diritto nazionale attraverso il “recepimento”. In genere, ciò deve essere effettuato entro due anni dall’adozione della direttiva.
https://eurlex.europa.eu/IT/legalcontent/glossary/directive.html#:~:text=Una%20direttiva%20costituisce%20un%20atto,riguarda%20il%20risultato%20da%20raggiungere.', 'Direttiva relativa alla lotta contro la frode che lede gli interessi finanziari dell''Unione mediante il diritto penale', 'Direttiva (UE) - 2017/1371', 'Direttiva (UE) 2017/1371', NULL);

-- Record 2525/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (678, 'Decreto Legislativo 6 settembre 2024, n. 125 Attuazione della direttiva 2022/2464/UE del Parlamento europeo e del Consiglio del 14 dicembre 2022, recante modifica del regolamento 537/2014/UE, della direttiva 2004/109/CE, della direttiva 2006/43/CE e della direttiva 2013/34/UE per quanto riguarda la rendicontazione societaria di sostenibilità', 'Decreto (IT)', '125/2024', 'Il Decreto legislativo 6 settembre 2024, n. 125 attua la Direttiva 2022/2464/UE (nota come Corporate Sustainability Reporting Directive, CSRD). Questo decreto introduce nuovi obblighi di rendicontazione societaria di sostenibilità per le imprese, ampliando notevolmente le informazioni che devono essere incluse nei bilanci aziendali in materia di sostenibilità ambientale, sociale e di governance (ESG).', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Attuazione della direttiva 2022/2464/UE (CSRD)', 'Decreto Legislativo - 125/2024', 'D.Lgs. 125/2024', 'Attuazione della direttiva 2022/2464/UE (CSRD)');

-- Record 2526/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (678, 'Principi di Corporate Governance G20/OECD (2023)- Principio V', 'Principi G20/OECD', 'Principio V', 'Il principio V stabilische che il quadro di governance aziendale dovrebbe garantire l''orientamento strategico dell''azienda, un monitoraggio efficace della gestione da parte del consiglio di amministrazione e la responsabilità del consiglio nei confronti dell''azienda e degli azionisti.', 'I Principi di Corporate Governance G20/OCSE sono lo standard internazionale per il governo societario. I Principi aiutano i responsabili politici a valutare e migliorare il quadro legale, regolamentare e istituzionale per il governo societario, con l''obiettivo di supportare l''efficienza economica, la crescita sostenibile e la stabilità finanziaria. Per maggiori informazioni vedere: https://legalinstruments.oecd.org/public/doc/322/322.en.pdf', 'Le responsabilità del consiglio di amministrazione', 'Principi - Corporate Governance G20/OECD - V', 'Principi G20/OECD: V.C.', 'Il consiglio di amministrazione dovrebbe adottare elevati standard etici.');

-- Record 2527/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (678, 'Legge 6 novembre 2012, n. 190 - Disposizioni per la prevenzione e la repressione della corruzione e dell''illegalità nella pubblica amministrazione', 'Legge (IT)', '190/2012', 'La Legge 190/2012, è la normativa italiana che fornisce disposizioni per la prevenzione e la repressione della corruzione e dell''illegalità nella pubblica amministrazione.', 'Il termine legge viene usato nel diritto con una molteplicità di significati, si distingue in:
la norma giuridica; l’atto normativo (legge in senso materiale); l’atto giuridico attraverso il quale il potere legislativo esercita la sua funzione (legge in senso formale); l’insieme delle norme giuridiche che costituiscono l’ordinamento giuridico, ossia il diritto oggettivo. Per maggiori informazioni accedere al link:
https://www.governo.it/it/costituzione-italiana/principi-fondamentali/2839', 'Disposizioni per la prevenzione e la repressione della corruzione e dell''illegalità nella pubblica amministrazione', 'Legge (IT) - 190/2012', 'Legge 190/2012', NULL);

-- Record 2528/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (678, 'SASB Standard- Servizi professionali e commerciali (2018) (SV-PS)', 'SASB', 'SV-PS', 'Gli Standard SASB sono progettati per identificare e standardizzare la divulgazione delle questioni di sostenibilità più rilevanti per le decisioni degli investitori in ciascuno dei 77 settori industriali. Lo standard dell''industria Servizi professionali e commerciali rientra nel settore Servizi.', 'Il Sustainability Accounting Standards Board (SASB) è un''organizzazione no-profit e indipendente che stabilisce gli standard. Gli standard SASB aiutano le aziende a divulgare informazioni rilevanti sulla sostenibilità ai loro investitori.
  
  Gli Standard SASB comprendono una vasta gamma di rischi e opportunità legati alla sostenibilità, inclusi informazioni ambientali e sociali così come governance operativa come la Gestione della Catena di Approvvigionamento, la Gestione del Rischio Sistemico ed Etica Aziendale, tra gli altri.
 https://sasb.ifrs.org/"', 'Servizi professionali e commerciali (2018)', 'SASB 2018: SV-PS', 'SASB 2018: SV-PS-510a.1.', 'Descrizione dell''approccio volto a garantire l''integrità professionale');

-- Record 2529/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (679, 'SDGs: Goal 6 Acqua pulita e servizi igienico-sanitari', 'SDGs', 'Goal 6', 'Garantire a tutti la disponibilità e la gestione sostenibile dell’acqua e delle strutture igienico-sanitarie', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Acqua pulita e servizi igienico-sanitari', 'SDGs - Goal 6', 'SDG 6.4', 'Aumentare considerevolmente entro il 2030 l''efficienza nell''utilizzo dell''acqua in ogni settore e garantire approvvigionamenti e forniture sostenibili di acqua potabile, per affrontare la carenza idrica e ridurre in modo sostanzioso il numero di persone che ne subisce le conseguenze');

-- Record 2530/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (679, 'Decreto Legislativo 152/2006 - Norme in materia ambientale', 'Decreto (IT)', '152/2006', 'Il Decreto Legislativo 3 aprile 2006, n. 152 stabilisce le norme fondamentali per la tutela dell''ambiente in Italia. Questo decreto, noto come "Codice dell''ambiente", regola diversi aspetti relativi alla gestione, alla protezione e alla valorizzazione dell''ambiente.', 'In Italia, i decreti sono atti normativi con forza di legge adottati in specifiche circostanze. Il Decreto Legislativo (d.lgs.), previsto dall''art. 76 della Costituzione, è emanato dal Governo su delega del Parlamento e pubblicato sulla Gazzetta Ufficiale, con entrata in vigore dopo 15 giorni. Il Decreto-Legge (d.l.), regolato dall''art. 77, è adottato dal Governo in casi straordinari di necessità e urgenza e deve essere convertito in legge dal Parlamento entro 60 giorni, altrimenti perde efficacia. Il Decreto del Presidente della Repubblica (d.p.r.) è un atto normativo subordinato alla legge, adottato dal Consiglio dei Ministri con il parere del Consiglio di Stato, emanato dal Presidente della Repubblica e pubblicato sulla Gazzetta Ufficiale.', 'Norme in materia ambientale', 'Decreto Legislativo - 152/2006', 'D.Lgs. 152/2006', NULL);

-- Record 2531/2644
