INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (684, 'GRI 305- Emissioni (2016)', 'GRI', '305', 'Questo Standard tratta il tema delle emissioni nell’aria – sostanze scaricate da una fonte nell’atmosfera. I vari tipi di emissioni sono: gas a effetto serra (GHG), sostanze che riducono lo strato di ozono (ODS, ozone-depleting substances), ossidi di azoto (NO) e ossidi di zolfo (SO), oltre ad altre emissioni nell’aria di entità notevole.', 'Gli Standard GRI rappresentano le buone pratiche per la reportistica pubblica in merito a una gamma di impatti economici, ambientali e sociali. La rendicontazione di sostenibilità basata sugli standard fornisce informazioni sui contributi positivi o negativi di un’organizzazione allo sviluppo sostenibile. Per maggiore informazioni visitare il sito GRI: https://www.globalreporting.org/how-to-use-the-gri-standards/gri-standards-italian-translations/', 'Emissioni (2016)', 'GRI - 305 (2016)', 'GRI 305-1 (2016)', 'Emissioni di gas a effetto serra (GHG) dirette (Scope 1)');

-- Record 2610/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (684, 'ISO 14064: Emissioni Gas Serra', 'ISO', '14064', 'L''ISO 14064 è una serie di standard internazionali che forniscono linee guida e requisiti per la quantificazione, il monitoraggio e la comunicazione delle emissioni di gas serra (GHG, Greenhouse Gas).', 'Gli standard ISO sono una serie di norme internazionali sviluppate dall''ISO che coprono una vasta gamma di settori e sono progettati per garantire, tra altri, qualità, sicurezza, efficienza e interoperabilità di prodotti, servizi e sistemi nelle organizzazioni. Per maggiori informazioni vedere: https://www.iso.org/home.html', 'Emissioni Gas Serra', 'ISO - 14064', 'ISO 14064', 'Emissioni Gas Serra');

