INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (684, 'Piano Strategico Nazionale Mobilità Sostenibile - PSNMS- (2019-2033)', 'Piano Nazionale (IT)', 'PSNMS', 'Previsto con la legge di Bilancio 2017, approvato con DPCM 30.04.2019 su proposta del MiMS, il Piano ha come obiettivi: il rinnovo del parco mezzi su gomma, attraverso la sostituzione di quelli maggiormente energivori ed inquinanti; il miglioramento della qualità dell’aria, la riduzione delle emissioni climalteranti e del particolato.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Piano Strategico Nazionale Mobilità Sostenibile', 'Piano Nazionale (IT) - PSNMS', 'PSNMS', 'Piano Strategico Nazionale Mobilità Sostenibile');

