INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (684, 'Piano Nazionale Adattamento Cambiamenti Climatici (PNACC)', 'Piano Nazionale (IT)', 'Adattamento Cambiamento Climatico', 'La finalità del Piano è contenere la vulnerabilità dei sistemi naturali, sociali ed economici agli impatti dei cambiamenti climatici e aumentarne la resilienza. Rappresenta lo strumento di attuazione della Strategia Nazionale di adattamento ai cambiamenti climatici del 2015 per la pianificazione nazionale a supporto delle istituzioni che saranno chiamate a sviluppare sulla propria scala di governo i contenuti del piano, tenendo conto delle specificità dei diversi contesti.', 'Un piano nazionale è un documento strategico e programmatico adottato dal governo per affrontare specifiche sfide o perseguire obiettivi di interesse nazionale.', 'Cambiamento Climatico (PNACC)', 'Piano Nazionale (IT) Adattamento Cambiamento Climatico', 'PNACC', 'Piano Nazionale Adattamento Cambiamenti Climatici');

-- Record 2618/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (684, 'Protocollo di Montreal relativo a sostanze che riducono lo strato di ozono', 'Protocollo (Int.)', 'Sostanze che riducono lo strato di ozono (Protocollo Montreal)', 'Il protocollo di Montreal (alla convenzione di Vienna per la protezione dello strato di ozono) è un accordo globale volto a proteggere lo strato di ozono stratosferico terrestre eliminando gradualmente le sostanze chimiche che lo riducono. Tale progressiva riduzione riguarda sia la produzione che il consumo di sostanze che riducono lo strato di ozono (ODS). Il suo emendamento più recente, l’emendamento Kigali, richiede la graduale riduzione graduale degli HFC.', 'Un protocollo è un tipo di accordo formale tra Stati o altre entità sovrane che integra, modifica o specifica ulteriormente un trattato esistente. I protocolli sono strumenti giuridici che possono avere diverse funzioni e scopi, tra cui l''espansione di disposizioni di un trattato, la creazione di nuovi obblighi o la regolamentazione di questioni specifiche non completamente coperte dal trattato originale.', 'Protocollo relativo a sostanze che riducono lo strato di ozono', 'Protocollo - Sostanze che riducono lo strato di ozono (Protocollo Montreal)', 'Protocollo Montreal', 'Protocollo relativo a sostanze che riducono lo strato di ozono');

-- Record 2619/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (684, 'Quadro per le politiche dell''energia e del clima 2030 (UE)', 'Strategia (UE)', 'Politiche dell''energia e del clima 2030', 'Il Quadro europeo per il clima e l''energia al 2030 è una strategia dell''Unione Europea (UE) che stabilisce obiettivi ambiziosi per ridurre le emissioni di gas serra e promuovere fonti energetiche rinnovabili entro il 2030', 'Le strategie dell''UE sono obiettivi politici generali dell''UE che vengono definiti collettivamente dalle istituzioni europee. Per maggiore informazione consulate il link: https://commission.europa.eu/strategy-and-policy_en', 'Quadro per le politiche dell''energia e del clima 2030 dell''UE', 'Strategia (UE) - Politiche dell''energia e del clima 2030', 'Politiche (UE) dell''energia e del clima 2030', 'Quadro per le politiche dell''energia e del clima 2030 dell''UE');

