INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (684, 'Strategia Nazionale per lo Sviluppo Sostenibile (SNSvS)', 'Strategia Nazionale (IT)', 'SNSvS', 'In Italia rappresenta il quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali, declinando per il contesto nazionale gli Obiettivi di Sviluppo Sostenibile (SDG) delineati dall''ONU', 'Una strategia nazionale rappresenta un quadro di azione orientato alla promozione di uno sviluppo che armonizzi aspetti economici, sociali e ambientali del paese. Ad esempio la Strategia Nazionale per lo sviluppo sostenibile, la Strategia Nazionale per il cambio climatico. Per maggiori informazioni accedere al link: https://www.mase.gov.it/pagina/strategia-nazionale-lo-sviluppo-sostenibile', 'Strategia Nazionale italiana per lo Sviluppo Sostenibile', 'SNSvS (2022)', 'SNSvS22- Pianeta III.1', 'Promuovere il presidio e la manutenzione del territorio e rafforzare le capacità di resilienza di comunità e territori anche in riferimento agli impatti dei cambiamenti climatici');

-- Record 2627/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (685, 'SDGs: Goal 14 La vita sott’acqua', 'SDGs', 'Goal 14', 'Conservare e utilizzare in modo durevole gli oceani, i mari e le risorse marine per uno sviluppo sostenibile', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'La vita sott’acqua', 'SDGs - Goal 14', 'SDG 14.1', 'Entro il 2025, prevenire e ridurre in modo significativo ogni forma di inquinamento marino, in particolar modo quello derivante da attività esercitate sulla terraferma, compreso l’inquinamento dei detriti marini e delle sostanze nutritive');

-- Record 2628/2644
INSERT INTO esg_impact.form_legal_bases
(question_id, main_legal_base, legal_base, legal_sub_base, main_base_descriptoin, legal_base_description, sub_base_description, legal_base_criteria, specific_map, specific_map_definition)
VALUES (685, 'SDGs: Goal 6 Acqua pulita e servizi igienico-sanitari', 'SDGs', 'Goal 6', 'Garantire a tutti la disponibilità e la gestione sostenibile dell’acqua e delle strutture igienico-sanitarie', 'I 17 Obiettivi di Sviluppo Sostenibile (SDGs), adottati dai membri delle Nazioni Unite con l''Agenda 2030 per lo Sviluppo Sostenibile, sono un urgente appello all''azione da parte di tutti i paesi - sviluppati e in via di sviluppo - in un partenariato globale. Riconoscono che porre fine alla povertà e ad altre privazioni deve andare di pari passo con strategie che migliorino la salute e l''istruzione, riducano le disuguaglianze e stimolino la crescita economica, il tutto affrontando il cambiamento climatico e lavorando per preservare i nostri oceani e le nostre foreste. Per maggiori informazioni consultare il sito: https://sdgs.un.org/goals#history', 'Acqua pulita e servizi igienico-sanitari', 'SDGs - Goal 6', 'SDG 6.3', 'Migliorare entro il 2030 la qualità dell''acqua eliminando le discariche, riducendo l''inquinamento e il rilascio di prodotti chimici e scorie pericolose, dimezzando la quantità di acque reflue non trattate e aumentando considerevolmente il riciclaggio e il reimpiego sicuro a livello globale.');

